mapper.select:
select

from public.auth_user

	del_status =0

		and user_id = #{userId,jdbcType=BIGINT}


		and cust_code = #{custCode,jdbcType=VARCHAR}


		and org_code = #{orgCode,jdbcType=VARCHAR}


		and user_name = #{userName,jdbcType=VARCHAR}


		and user_old_name = #{userOldName,jdbcType=VARCHAR}


		and real_name = #{realName,jdbcType=VARCHAR}


		and user_passwd = #{userPasswd,jdbcType=VARCHAR}


		and user_identity = #{userIdentity,jdbcType=VARCHAR}


		and login_ip = #{loginIp,jdbcType=VARCHAR}


		and login_time = #{loginTime,jdbcType=TIMESTAMP}


		and user_status = #{userStatus,jdbcType=INTEGER}


		and record_time = #{recordTime,jdbcType=TIMESTAMP}


		and entry_user_id = #{entryUserId,jdbcType=BIGINT}


		and operation_user_id = #{operationUserId,jdbcType=BIGINT}


		and opretion_time = #{opretionTime,jdbcType=TIMESTAMP}


		and user_phone = #{userPhone,jdbcType=VARCHAR}


order by user_id
